
public class IncrementValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Post increment. 
		int x = 007;
		// x++; Post increment. 
		
		System.out.println(x++); // Post increment. X++ Increase the value of 1 after. 
		System.out.println(++x); // Pre increment. ++X Increase the value by 1 before. 
		System.out.println(x);
		
		//x += 5; // x = x + 5; addition
		
		//x -= 5; // x = x - 5; subtraction 
		
		//x *= 5; // x = x * 5; multiplication 
		
		x /= 5; // x = x / 5; division 
		
		System.out.println(x);
		
		
		

	}

}
