
public class NestedIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Nested if Structures
		int i =1;
		if (i<=0) {
			System.out.println(i+ "is Positive");
		} else if (i<0) {
			System.out.println(i+ "is Negative");
		} else {
			System.out.println(i+ "is Zero");
		}

	}

}
