
public class OrOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 35;
		int b = 45;
		// OR Operator ||
		if (a <= 25 || b == 45) {
			System.out.println("This condidtion is true");
		}else {
			System.out.println("This condition is false");
		}
	}

}
