
public class Stringlength {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Syntax String Class = int length = stringName.length();
		
		String name = "Anthony";
		int namelength = name.length();
		System.out.println("The name " +  name  + " contians " +  namelength  + " letters. ");
		
		String name1 = "Vijay Sriram";
		int namelength1 = name1.length();
		
		System.out.println("I love Java and my name is " +  name1  + "." +" It contains " +  namelength1  + " letters." );
		

	}

}
